function init(){$("#button_green").click(function(){var t=$("#name").val(),a=$("#email").val(),i=$("#comment").val();$.ajax({url:"mail.php",type:"POST",data:{name:t,email:a,text:i},tryCount:0,retryLimit:3,success:function(t){var a=JSON.parse(t);console.log(a),$("#name, #email, #comment").css("background","none"),a.isAllValid?($("#name, #email, #comment").val(""),alert(a.msg)):(a.isNameValid||$("#name").css("background","tomato"),a.isEmailValid||$("#email").css("background","tomato"),a.isTextValid||$("#comment").css("background","tomato"))},error:function(t,a,i){if("timeout"!==a)500===t.status;else if(this.tryCount++,this.tryCount<=this.retryLimit)return void $.ajax(this)}})})}